mapper.select:
select 'true' as QUERYID,

from stu_course
where course_id = #{courseId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}

mapper.delete:
delete from stu_course
where course_id = #{courseId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}

mapper.insert:
insert into stu_course (course_id, stu_id, stu_grade
  )
values (#{courseId,jdbcType=INTEGER}, #{stuId,jdbcType=INTEGER}, #{stuGrade,jdbcType=REAL}
  )

mapper.update:
update stu_course


    stu_grade = #{stuGrade,jdbcType=REAL},


where course_id = #{courseId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}

update stu_course
set stu_grade = #{stuGrade,jdbcType=REAL}
where course_id = #{courseId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
