mapper.select:
select 'true' as QUERYID,

from wechat_bind
where stu_id = #{stuId,jdbcType=INTEGER}
  and Wechat_UnionID = #{wechatUnionid,jdbcType=CHAR}

mapper.delete:
delete from wechat_bind
where stu_id = #{stuId,jdbcType=INTEGER}
  and Wechat_UnionID = #{wechatUnionid,jdbcType=CHAR}

mapper.insert:
insert into wechat_bind (stu_id, Wechat_UnionID, stu_name
  )
values (#{stuId,jdbcType=INTEGER}, #{wechatUnionid,jdbcType=CHAR}, #{stuName,jdbcType=CHAR}
  )

mapper.update:
update wechat_bind


    stu_name = #{stuName,jdbcType=CHAR},


where stu_id = #{stuId,jdbcType=INTEGER}
  and Wechat_UnionID = #{wechatUnionid,jdbcType=CHAR}

update wechat_bind
set stu_name = #{stuName,jdbcType=CHAR}
where stu_id = #{stuId,jdbcType=INTEGER}
  and Wechat_UnionID = #{wechatUnionid,jdbcType=CHAR}
